        public IconSelector()
        {
            this.InitializeComponent();
            Style style = this.FindResource("PathStyleMain") as Style;
            foreach (string icon in this.icons)
            {
                this.AddIconToList(icon, style);
            }
        }
        private void AddIconToList(string content, Style style)
        {
            Path path = new()
            {
                Data = Geometry.Parse(content),
                Style = style
            };
            this.List.Items.Add(path);
        }

        private void List_SelectionChanged(object sender, SelectionChangedEventArgs e)
        {
            ListBox lb = (ListBox)sender;
            Path path = (Path)lb.SelectedItem;
            this.SelectedIcon = path.Data.ToString();
            this.IconTest.Data = path.Data;
        }

        private void ClickSave(object sender, RoutedEventArgs e)
        {
            this.result = true;
            OnResult?.Invoke(true);
        }
    }
